        cip_structure.variable_name = "Yes"
        cip_structure.variable_type_name = 'YesType'
        cip_structure.add_member('bool_1', CIPBoolean())
        cip_structure['bool_1'] = True
        cip_structure.add_member('bool_2', CIPBoolean())
